import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../redux/actions/authActions";
import "../styles/Login.css";
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS

const Login = () => {
  const initialState = { email: "", password: "" };
  const history = useHistory();
  const { auth } = useSelector((state) => state);
  const [showpass, setShowpass] = useState(false);
  const [userData, setUserData] = useState(initialState);
  const dispatch = useDispatch();

  useEffect(() => {
    if (auth.token) {
      history.push("/");
    }
  }, [auth.token, history]);

  const { email, password } = userData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(login(userData));
  };

  return (
    <div className="login container-fluid">
      <div className="row align-items-center vh-100">
        <div className="col-md-6 d-none d-md-block">
          <img
            src="https://via.placeholder.com/600x800" // Replace with your image URL
            alt="Login Visual"
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <div className="login-container">
            <h3 className="login-header">Social Network</h3>
            <h6 className="login-subheader">Login</h6>
            <form className="login-dataform" onSubmit={handleSubmit}>
              <div className="mb-3">
                <input
                  className="form-control"
                  type="email"
                  name="email"
                  value={email}
                  onChange={handleChange}
                  placeholder="Type your email"
                />
              </div>
              <div className="mb-3">
                <input
                  className="form-control"
                  type={showpass ? "text" : "password"}
                  placeholder="Type your password"
                  value={password}
                  name="password"
                  onChange={handleChange}
                />
              </div>
              <small
                className="login-showpass"
                onClick={() => setShowpass(!showpass)}
              >
                {showpass ? "Hide" : "Show"}
              </small>
              <div className="mb-3">
                <button className="btn btn-primary w-100" type="submit">
                  Log In
                </button>
              </div>
              <p className="login-small">
                Don't have an account? <Link to="/register">Create HERE</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
