import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../redux/actions/authActions";
import { Link, useLocation } from "react-router-dom";
import { getDataApi } from "../utils/fetchDataApi";
import UserCard from "./UserCard";
import LoadIcon from "../images/loading.gif";

import "../styles/Header.css";
import { Avatar } from "@material-ui/core";
import {
  Home,
  Message,
  Notifications,
  Search,
  Explore,
  ExitToApp,
} from "@material-ui/icons";

export const Header = () => {
  const [search, setSearch] = useState("");
  const [users, setUsers] = useState([]);
  const [load, setLoad] = useState(false);

  const dispatch = useDispatch();
  const { auth, notify } = useSelector((state) => state);
  const { pathname } = useLocation();

  const handleClose = () => {
    setSearch("");
    setUsers([]);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!search) return;

    try {
      setLoad(true);
      const res = await getDataApi(`search?username=${search}`, auth.token);
      setUsers(res.data.users);
      setLoad(false);
    } catch (err) {
      dispatch({
        type: "ALERT",
        payload: {
          error: err.response.data.msg,
        },
      });
    }
  };

  return (
    <div className="header container-fluid d-flex justify-content-between align-items-center bg-primary text-white p-2 sticky-top">
      <div className="header-brand">
        <h3 className="text-white">Social Network</h3>
      </div>
      <form
        className="header-center position-relative d-flex align-items-center w-50"
        onSubmit={handleSearch}
      >
        <input
          type="text"
          className="form-control me-2"
          placeholder="Search Profiles"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit" className="btn btn-outline-light d-none">
          Search
        </button>
        <Search
          className="position-absolute top-50 end-0 translate-middle-y me-2"
          style={{ opacity: users.length > 0 ? "0" : "1" }}
        />
        <span
          className="header-centersearchclose position-absolute top-50 end-0 translate-middle-y me-4 cursor-pointer"
          onClick={handleClose}
          style={{ opacity: users.length > 0 ? "1" : "0" }}
        >
          &times;
        </span>
        <div className="header-searchusers position-absolute bg-primary w-100 mt-2 p-2 rounded">
          {load && (
            <img
              src={LoadIcon}
              alt=""
              style={{ width: "48px", height: "48px" }}
            />
          )}
          {search &&
            users.length > 0 &&
            users.map((user) => (
              <UserCard key={user._id} user={user} handleClose={handleClose} />
            ))}
        </div>
      </form>
      <div className="header-left d-flex align-items-center">
        <Link
          to={`/profile/${auth.user._id}`}
          className="d-flex align-items-center text-decoration-none me-2 text-white"
        >
          <Avatar src={auth.user.avatar} className="me-2" />
          <h5 className="m-0">{auth.user.fullname}</h5>
        </Link>
        <Link to="/" className="btn btn-outline-light me-2">
          <Home />
        </Link>
        <Link to="/message" className="btn btn-outline-light me-2">
          <Message />
        </Link>
        <Link
          to="/notification"
          className="btn btn-outline-light me-2 position-relative"
        >
          <Notifications />
          {notify && notify.data.length > 0 && (
            <span className="badge bg-danger position-absolute top-0 start-100 translate-middle">
              {notify.data.length}
            </span>
          )}
        </Link>
        <Link to="/explore" className="btn btn-outline-light me-2">
          <Explore />
        </Link>
        <button
          className="btn btn-outline-light"
          onClick={() => dispatch(logout())}
        >
          <ExitToApp />
        </button>
      </div>
    </div>
  );
};

export default Header;
