import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { login } from "../redux/actions/authActions";
import "../styles/Login.css"; // Ensure you uncomment this and create a CSS file

// Import the image
import loginImage from "../images/login.png";

const Login = () => {
  const initialState = { email: "", password: "" };
  const history = useHistory();
  const { auth } = useSelector((state) => state);
  const [showpass, setShowpass] = useState(false);
  const [userData, setUserData] = useState(initialState);
  const dispatch = useDispatch();

  useEffect(() => {
    if (auth.token) {
      history.push("/");
    }
  }, [auth.token, history]);

  const { email, password } = userData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(login(userData));
  };

  return (
    <div className="login-container d-flex align-items-center justify-content-center">
      <div className="login-card shadow-lg">
        <div className="row no-gutters">
          <div className="col-md-6 d-none d-md-flex align-items-center">
            <img
              src={loginImage}
              alt="Login Visual"
              className="img-fluid login-image"
            />
          </div>
          <div className="col-md-6 d-flex flex-column justify-content-center p-4">
            <h3 className="text-center mb-3">Welcome Back</h3>
            <h6 className="text-center text-muted mb-4">
              Login to your account
            </h6>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <input
                  className="form-control mb-3"
                  type="email"
                  name="email"
                  value={email}
                  onChange={handleChange}
                  placeholder="Email"
                  required
                />
              </div>
              <div className="form-group position-relative">
                <input
                  className="form-control mb-3"
                  type={showpass ? "text" : "password"}
                  placeholder="Password"
                  value={password}
                  name="password"
                  onChange={handleChange}
                  required
                />
                <i
                  className={`login-showpass ${showpass ? "hide" : "show"}`}
                  onClick={() => setShowpass(!showpass)}
                >
                  {showpass ? "Hide" : "Show"}
                </i>
              </div>
              <button className="btn btn-primary w-100 mb-3" type="submit">
                Log In
              </button>
              <p className="text-center">
                Don't have an account? <Link to="/register">Create HERE</Link>
              </p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
