import React, { useState } from "react";
import "../styles/PostCard.css";

const PostCardBody = ({ pos }) => {
  const [readMore, setReadMore] = useState(false);
  const [currentImage, setCurrentImage] = useState(0);

  const showNextImage = () => {
    if (currentImage < pos.images.length - 1) {
      setCurrentImage(currentImage + 1);
    }
  };

  const showPrevImage = () => {
    if (currentImage > 0) {
      setCurrentImage(currentImage - 1);
    }
  };

  return (
    <div
      className="postcardbody"
      style={{ margin: "1rem 0", padding: "1rem 0" }}
    >
      <div className="postcardbodycontent">
        {pos.content && pos.content.length < 60
          ? pos.content
          : readMore
          ? `${pos.content}...`
          : `${pos.content.slice(0, 60)}...`}
        {pos.content && pos.content.length > 60 && (
          <span
            style={{ color: "black", cursor: "pointer" }}
            onClick={() => setReadMore(!readMore)}
          >
            {readMore ? "Hide " : "Show "}
          </span>
        )}
      </div>
      <div className="postcardbodyimage">
        <span className="postcardbodyimagenext" onClick={showNextImage}>
          {" "}
          {">"}{" "}
        </span>
        <span className="postcardbodyimageprev" onClick={showPrevImage}>
          {" "}
          {"<"}{" "}
        </span>
        {pos.images &&
          pos.images.length > 0 &&
          pos.images.map(
            (image, index) =>
              index === currentImage && (
                <div className="postcardbodyimages" key={index}>
                  {image.secure_url.match(/video/i) ? (
                    <video
                      controls
                      src={image.secure_url}
                      alt="video"
                      height="100%"
                      width="100%"
                    />
                  ) : (
                    <img src={image.secure_url} alt="image" />
                  )}
                </div>
              )
          )}
      </div>
    </div>
  );
};

export default PostCardBody;
