import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateProfile } from "../redux/actions/profileActions";
import "../styles/EditProfile.css";
import { checkimage } from "../utils/imageupload";

const EditProfile = ({ user, SetOnEdit }) => {
  const { auth } = useSelector((state) => state);
  const dispatch = useDispatch();

  const initState = {
    website: "",
    fullname: "",
    story: "",
    phone: "",
    address: "",
  };
  const [editData, setEditData] = useState(initState);
  const { website, fullname, story, phone, address } = editData;
  const [avatar, setAvatar] = useState("");
  ///why you need here is i will be come back note to me.

  const changeavatar = (e) => {
    const file = e.target.files[0];
    const err = checkimage(file);
    if (err) return dispatch({ type: "ALERT", payload: { error: err } });
    setAvatar(file);
  };
  useEffect(() => {
    setEditData(user);
  }, [user]);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setEditData({ ...editData, [name]: value });
  };
  const selectupload = () => {
    const fileuploadinput = document.getElementById("file-upload");
    fileuploadinput.click();
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(updateProfile({ editData, avatar, auth }));
    SetOnEdit(false);
  };
  return (
    <div className="editprofile">
      <div className="container editprofile-content">
        <div className="editprofile-head d-flex justify-content-between align-items-center mb-3">
          <h4 className="editprofile-headtitle m-0">Edit Your Profile</h4>
          <button className="btn btn-close" onClick={() => SetOnEdit(false)}>
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="editprofile-avatar d-flex justify-content-center align-items-center mb-3">
          <div className="avatar-wrapper">
            <img
              src={avatar ? URL.createObjectURL(avatar) : auth.user.avatar}
              alt="Profile Avatar"
              className="rounded-circle"
            />
            <div className="edit-icon" onClick={selectupload}>
              <i className="fas fa-camera">
                <p className="editprofile-userdatapara d-inline">Change Pic</p>
              </i>
            </div>
          </div>
          <input
            style={{ display: "none" }}
            type="file"
            id="file-upload"
            accept="image/*"
            onChange={changeavatar}
          />
        </div>
        <div className="editprofile-userdata">
          <div className="mb-3">
            <label htmlFor="fullname" className="form-label">
              Full Name
            </label>
            <input
              type="text"
              value={fullname}
              onChange={handleChangeInput}
              name="fullname"
              className="form-control"
              placeholder="Type your name"
            />
            <small className="text-muted">{fullname.length}/25</small>
          </div>
          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              value={address}
              onChange={handleChangeInput}
              name="address"
              className="form-control"
              placeholder="Type your address"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="website" className="form-label">
              Website
            </label>
            <input
              type="text"
              value={website}
              onChange={handleChangeInput}
              name="website"
              className="form-control"
              placeholder="Type your website name"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="phone" className="form-label">
              Phone
            </label>
            <input
              type="text"
              value={phone}
              onChange={handleChangeInput}
              name="phone"
              className="form-control"
              placeholder="Type your phone number"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="story" className="form-label">
              Story
            </label>
            <textarea
              value={story}
              onChange={handleChangeInput}
              name="story"
              className="form-control"
              placeholder="Type your Bio"
              rows="3"
            ></textarea>
            <small className="text-muted">{story.length}/200</small>
          </div>
          <button onClick={handleSubmit} className="btn btn-primary w-100">
            Submit
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
