import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateProfile } from "../redux/actions/profileActions";
import { checkimage } from "../utils/imageupload";

const EditProfile = ({ user, SetOnEdit }) => {
  const { auth } = useSelector((state) => state);
  const dispatch = useDispatch();

  const initState = {
    website: "",
    fullname: "",
    story: "",
    phone: "",
    address: "",
  };
  const [editData, setEditData] = useState(initState);
  const { website, fullname, story, phone, address } = editData;
  const [avatar, setAvatar] = useState("");

  const changeAvatar = (e) => {
    const file = e.target.files[0];
    const err = checkimage(file);
    if (err) return dispatch({ type: "ALERT", payload: { error: err } });
    setAvatar(file);
  };

  useEffect(() => {
    setEditData(user);
  }, [user]);

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setEditData({ ...editData, [name]: value });
  };

  const selectUpload = () => {
    const fileUploadInput = document.getElementById("file-upload");
    fileUploadInput.click();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(updateProfile({ editData, avatar, auth }));
    SetOnEdit(false);
  };

  return (
    <div className="editprofile">
      <div className="container editprofile-content py-4">
        <div className="editprofile-head d-flex justify-content-between align-items-center mb-3">
          <h4 className="editprofile-headtitle m-0">Edit Your Profile</h4>
          <button
            className="btn-close"
            onClick={() => SetOnEdit(false)}
            aria-label="Close"
          ></button>
        </div>
        <div className="editprofile-avatar text-center mb-3">
          <img
            src={avatar ? URL.createObjectURL(avatar) : auth.user.avatar}
            alt="Profile Avatar"
            className="rounded-circle img-thumbnail"
            width="150"
          />
          <div className="mt-2">
            <button className="btn btn-outline-primary" onClick={selectUpload}>
              <i className="fas fa-camera me-2"></i> Change Pic
            </button>
            <input
              type="file"
              id="file-upload"
              accept="image/*"
              style={{ display: "none" }}
              onChange={changeAvatar}
            />
          </div>
        </div>
        <form onSubmit={handleSubmit} className="editprofile-userdata">
          <div className="mb-3">
            <label htmlFor="fullname" className="form-label">
              Full Name
            </label>
            <input
              type="text"
              name="fullname"
              value={fullname}
              onChange={handleChangeInput}
              className="form-control"
              placeholder="Type your name"
              maxLength="25"
            />
            <small className="text-muted">{fullname.length}/25</small>
          </div>
          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              name="address"
              value={address}
              onChange={handleChangeInput}
              className="form-control"
              placeholder="Type your address"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="website" className="form-label">
              Website
            </label>
            <input
              type="text"
              name="website"
              value={website}
              onChange={handleChangeInput}
              className="form-control"
              placeholder="Type your website name"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="phone" className="form-label">
              Phone
            </label>
            <input
              type="text"
              name="phone"
              value={phone}
              onChange={handleChangeInput}
              className="form-control"
              placeholder="Type your phone number"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="story" className="form-label">
              Story
            </label>
            <textarea
              name="story"
              value={story}
              onChange={handleChangeInput}
              className="form-control"
              placeholder="Type your Bio"
              rows="3"
              maxLength="200"
            ></textarea>
            <small className="text-muted">{story.length}/200</small>
          </div>
          <button type="submit" className="btn btn-primary w-100">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default EditProfile;
